import React, { Component } from "react";
import Axios from "axios";
import { connect } from "react-redux";

class Room extends Component {
  render() {
    return (
      <div>
        {/* room */}
        <div className="card mt-4">
          <div className="card mt-4">
            <div className="card-title">
              <h3 className="card-title ">{this.props.room.room_name}</h3>
              <img
                className="card-img-top"
                src="http://picsum.photos/200/200"
                width="200"
                height="300"
              />

              {/* btn edit room */}
              <button
                className="btn btn-primary"
                data-toggle="modal"
                data-target="#modelRoom"
              >
                <i class="fa fa-edit"></i>
              </button>

              {/* btn remove room */}
              <button
                className="btn btn-danger"
                type="button"
                onClick={() => {
                  this.deleteRoom(this.props.room.room_id);
                }}
              >
                <i class="fa fa-times-circle"></i>
              </button>
            </div>
            <div className="card-body">
              <div className="row">
                <div className="col-6">
                  <h4>Temperter</h4>
                  <p>
                    28 <span>*C</span>
                  </p>
                </div>
                <div className="col-6">
                  <h4>Huminity</h4>
                  <p>150</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  deleteRoom = (room_id) => {
    //  Delete Room with id
    let promise = Axios({
      method: "DELETE",
      url: "http://localhost:8000/rooms/:id",
    })
      .then((res) => {
        alert(res.data);
        this.props.room.getRoomList();
      })
      .catch((err) => {
        console.log(err);
      });
  };
}

export default connect()(Room);
