import React, { Component, useState, useEffect } from "react";
import "./Device.css";
import ModalDevice from "./ModalDevice";
// socket
import { io } from "socket.io-client";
const socket = io.connect("http://localhost:8080/Room", {
  transports: ["websocket", "polling", "flashsocket"],
});

export default function Device() {
  const [ledStatus, setLedStatus] = useState(0);

  useEffect(() => {
    var json = {
      led: ledStatus,
    };
    socket.emit("LED", ledStatus);

    socket.on("connect", () => {
      console.log("socket connected");
    });
  }, []);

  const handleChange = (event) => {
    setLedStatus({
      [event.target.name]: event.target.value,
    });
  };

  return (
    <div className="card ">
      <div className="card-title">
        <img
          className="card-img-top"
          src="http://picsum.photos/200/200"
          width="200"
          height="300"
        />

        <button
          className="btn btn-primary"
          data-toggle="modal"
          data-target="#modelDevice"
        >
          <i class="fa fa-edit"></i>
        </button>

        <button className="btn btn-danger">
          <i class="fa fa-times-circle"></i>
        </button>
      </div>
      <div className="card-body">
        <p className="card-title">Open</p>

        <label className="switch">
          <input type="checkbox" value={ledStatus} />
          <span className="slider round" />
        </label>
      </div>
    </div>
  );
}
