let defaultState = {
  roomList: [],
  roomEdit: [],
  roomRedux,
};

const RoomReducer = (state = defaultState, action) => {
  switch (action.type) {
    case "FETCH_ROOM": {
      state.roomList = action.payload;
      return { ...state };
    }

    case "DELETE_ROOM": {
      let roomUpdate = [...state.roomList];

      roomUpdate = roomUpdate.filter((room) => room.room_id !== action.room_id);
      console.log("room update", roomUpdate);
      // cap nhat lai state gio hang
      state.roomList = roomUpdate;
      return { ...state };
    }

    case "EDIT_ROOM": {
      //cap nhat la state
      state.roomEdit = { ...action.payload };
      console.log("edit room", state.roomEdit);

      let newRoomEdit = { ...state.roomEdit };
      newRoomEdit.values = { ...action.payload };

      return { ...state };
    }
    default:
      return state;
  }
};

export default RoomReducer;
