class ModalRoom extends Component {
  constructor(props) {
    super(props);

    this.state = { room_id: "", item_id: "", room_name: "" };
  }
  handleChangeInput = (event) => {
    return event.target.value;
  };

  onSubmitForm = (event) => {
    event.preventDefault(); // chan su kien submit cua browser

    Axios({
      method: "POST",
      url: "http://localhost:8000/rooms",
    })
      .then((res) => {
        this.props.dispatch({
          type: "ADD_ROOM",
          payload: res.data,
          name: res.data.room_name,
        });
      })
      .catch((err) => {
        console.log("add room err", err);
      });
  };
  render() {
    const { room_id, room_name } = this.state;
    return (
      <>
        <div>
          {/* Button trigger modal */}
          <button
            type="button"
            className="btn btn-primary btn-lg"
            data-toggle="modal"
            data-target="#modelRoom"
          >
            <i class="fa fa-plus"></i>
          </button>
          {/* Modal */}
          <div
            className="modal fade"
            id="modelRoom"
            tabIndex={-1}
            role="dialog"
            aria-labelledby="modelTitleId"
            aria-hidden="true"
          >
            <div className="modal-dialog" role="document">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title">Room</h5>
                  <button
                    type="button"
                    className="close"
                    data-dismiss="modal"
                    aria-label="Close"
                  >
                    <span aria-hidden="true">×</span>
                  </button>
                </div>
                <div className="modal-body">
                  <form onSubmit={this.onSubmitForm}>
                    <div>
                      <div className="form-group">
                        <label htmlFor="ID">ID</label>
                        <input
                          typeInput="text"
                          className="form-control"
                          name="idRoom"
                          disabled
                          name="room_id"
                          onChange={this.handleChangeInput}
                          value={room_id}
                        />
                      </div>
                      <div className="form-group">
                        <label htmlFor="Name">Name room</label>
                        <input
                          typeInput="text"
                          className="form-control"
                          name="room_name"
                          onChange={this.handleChangeInput}
                          value={room_name}
                        />
                      </div>
                      <div className="form-group">
                        <div className="row" style={{}}>
                          <label htmlFor="Image" className="col-2">
                            Image
                          </label>
                          <input
                            type="file"
                            className="col-10"
                            name="fileDevice"
                          />
                        </div>
                        <div id="image-show" />
                      </div>
                      <button type="submit" className="btn btn-success">
                        Submit
                      </button>
                    </div>
                  </form>
                </div>
                <div className="modal-footer">
                  <button
                    type="button"
                    className="btn btn-secondary"
                    data-dismiss="modal"
                  >
                    Close
                  </button>
                  <button
                    type="button"
                    className="btn btn-primary"
                    onClick={() => {
                      {
                        this.tryClick();
                      }
                    }}
                  >
                    Save
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </>
    );
  }

  tryClick = () => {
    console.log("name redux", this.props.room.room_name);
  };
}

export default connect()(ModalRoom);
